import { useEffect, useState } from "react";
import "./TopicsListItem.scss";

export const TopicsListItem = ({ info }) => {
  const [isChecked, setIsChecked] = useState(false);

  useEffect(() => {
    if (isChecked) {
      document.querySelector(".topicsListItem").style.backgroundColor = "red";
    }
    console.log(isChecked);
  }, [isChecked]);

  const handleClick = (e) => {
    const target = e.target.closest("li");
    setIsChecked(!isChecked);


    // target.children[1].checked = isChecked;
  };

  return (
    <li className="topicsListItem" onClick={(e) => handleClick(e)}>
      <input type="checkbox" value={isChecked} />
      <img src={info.image} />
      <span>{info.value}</span>
    </li>
  );
};
