import { Link } from "react-router-dom";
import "./HateContent.scss";
import { ConfirmButton } from "../ConfirmButton/ConfirmButton";
import { useState } from "react";

export const HateContent = () => {
  const [isCheckedOne, setIsCheckedOne] = useState(false);
  const [isCheckedTwo, setIsCheckedTwo] = useState(false);
  const [isCheckedThree, setIsCheckedThree] = useState(false);
  const [isCheckedFour, setIsCheckedFour] = useState(false);

  const hateArr = [
    "Lack of logic",
    "A slow speed",
    "Lack of humor",
    "Way too generic ending",
  ];

  const handleChecked = (e) => {
    const target = e.target;

    hateArr.forEach((el) => {
      if (el === target.textContent) {
        target.classList.add("checkedParent");
        target.children[1].classList.add("checkedChild");
        target.children[1].style.backgroundColor = "#e4229b";
      } else {
        target.classList.remove("checkedChild");
      }
    });
  };

  return (
    <div className="hateContentContainer">
      <span className="hateQuestionContent">
        What do you <span>hate</span> the most in a book?
      </span>

      <ul className="listOfOption">
        {hateArr.map((el) => (
          <li key={el} onClick={(e) => handleChecked(e)}>
            <span>{el}</span>
            <div className="checkbox" />
          </li>
        ))}
      </ul>

      <ConfirmButton>Next</ConfirmButton>
    </div>
  );
};
