import {
  CHOOSE_LANGUAGE,
  CHOOSE_GENDER,
  CHOOSE_AGE,
  CHOOSE_HATE,
  REMOVE_HATE,
  CHOOSE_TOPICS,
  ADD_EMAIL,
  IS_CHECKED_TOPIC,
} from "./action";

const defaultState = {
  language: "",
  gender: "",
  age: "",
  hateArr: [],
  topicsArr: [],
  email: "",
  isCheckedTopic: false,
};

export const reducer = (state = defaultState, action) => {
  switch (action.type) {
    case CHOOSE_LANGUAGE:
      return { ...state, language: action.payload };
    case CHOOSE_GENDER:
      return { ...state, gender: action.payload };
    case CHOOSE_AGE:
      return { ...state, age: action.payload };
    case CHOOSE_HATE:
      return { ...state, hateArr: [...state.hateArr, action.payload] };
    case REMOVE_HATE:
      return { ...state, hateArr: [...action.payload] };
    case CHOOSE_TOPICS:
      return { ...state, topicsArr: [...action.payload] };
    // case REMOVE_TOPICS:
    //   return { ...state, topicsArr: [...action.payload] };
    case IS_CHECKED_TOPIC:
      return { ...state, topicsArr: action.payload };
    default:
      return state;
  }
};
