import { ConfirmButton } from "../ConfirmButton/ConfirmButton";
import { QuestionContent } from "../QuestionContent/QuestionContent";
import { TopicsListItem } from "../TopicsListItem/TopicsListItem";
import "./TopicsContent.scss";

export const TopicsContent = () => {
  const contentArr = [
    "What are your favorite topics?",
    "Choose up to 3 topics you like",
  ];

  const topicsArr = [
    { value: "Werewolf", image: "" },
    { value: "Action", image: "" },
    { value: "Royal Obsession", image: "" },
    { value: "Romance", image: "" },
    { value: "Young Adult", image: "" },
    { value: "Bad Boy", image: "" },
    { value: "Billionaire", image: "" },
  ];

  return (
    <div className="topicsContentContainer">
      <QuestionContent content={contentArr} />
      <ul className="topicsList">
        {topicsArr.map((el) => (
          <TopicsListItem value={el} key={el.value} />
        ))}
      </ul>
      <ConfirmButton styleName="topicsBtn">Next</ConfirmButton>
    </div>
  );
};
