import "./HateContent.scss";
import { ConfirmButton } from "../ConfirmButton/ConfirmButton";
import { useDispatch, useSelector } from "react-redux";
import { chooseHate } from "../../redux/action";
import { useState } from "react";

export const HateContent = () => {
  const [next, setNext] = useState("");
  const hateArr = [
    "Lack of logic",
    "A slow speed",
    "Lack of humor",
    "Way too generic ending",
  ];
  const li = document.querySelectorAll("li");

  const dispatch = useDispatch();

  const handleChecked = (e) => {
    const target = e.target.closest("li");

    hateArr.forEach((el) => {
      if (target.textContent === el) {
        if (target.classList.contains("checkedParent")) {
          target.classList.remove("checkedParent");
          target.children[1].classList.remove("checkedChild");
          target.children[1].style.backgroundColor = "#6d4376";
        } else {
          target.classList.add("checkedParent");
          target.children[1].classList.add("checkedChild");
          target.children[1].style.backgroundColor = "#e4229b";
        }
      }
    });
  };

  const handleClick = () => {
    const hateArr = [];

    li.forEach((el) => {
      if (el.classList.contains("checkedParent")) {
        hateArr.push(el.innerText);
      }
    });
    dispatch(chooseHate(hateArr));

    if(hateArr.length !==0) {
        setNext('/quiz/5')
    }
  };

  return (
    <div className="hateContentContainer">
      <span className="hateQuestionContent">
        What do you <span>hate</span> the most in a book?
      </span>

      <ul className="listOfOption">
        {hateArr.map((el) => (
          <li key={el} onClick={(e) => handleChecked(e)}>
            <span>{el}</span>
            <div className="checkbox" />
          </li>
        ))}
      </ul>

      <ConfirmButton onClick={handleClick} goTo={next}>
        Next
      </ConfirmButton>
    </div>
  );
};
