import {
  CHOOSE_LANGUAGE,
  CHOOSE_GENDER,
  CHOOSE_AGE,
  CHOOSE_HATE,
  CHOOSE_TOPICS,
  ADD_EMAIL,
} from "./action";

const defaultState = {
  language: "",
  gender: "",
  age: "",
  hate: [
    { value: "Lack of logic", isChecked: false },
    { value: "A slow speed", isChecked: false },
    { value: "Lack of humor", isChecked: false },
    { value: "Way too generic ending", isChecked: false },
  ],
  topics: [],
  email: "",
};

export const reducer = (state = defaultState, action) => {
  switch (action.type) {
    case CHOOSE_LANGUAGE:
      return { ...state, language: action.payload };
    default:
      return state;
  }
};
