import { useEffect, useState } from "react";
import "./TopicsListItem.scss";
import { useDispatch, useSelector } from "react-redux";
import { isCheckedTopic } from "../../redux/action";

export const TopicsListItem = ({ info }) => {
  //   const [isChecked, setIsChecked] = useState(false);
  const dispatch = useDispatch();
  const isChecked = useSelector((state) => state.isCheckedTopic);

  useEffect(() => {
    const inputs = document.querySelectorAll("input");

    inputs.forEach((el) => {
      console.log(el.value);
      if (el.value === "true") {
        el.closest("li").classList.add("checkedItem");
      } else {
        el.closest("li").classList.remove("checkedItem");
      }
    });
  }, [isChecked]);

  const handleClick = () => {
    dispatch(isCheckedTopic(!isChecked))
  };

  return (
    <li className="topicsListItem" onClick={() => handleClick()}>
      <input type="checkbox" value={isChecked} />
      <img src={info.image} />
      <span>{info.value}</span>
    </li>
  );
};
