    const progressBarWidth =
      document.querySelector(".stepsProgressBar").offsetWidth;
    const progressWidth = (progressBarWidth / 5) * step;
    progress.style.width = progressWidth
//   };


//   useEffect(() => {
//     handleProgress();
//   }, [step]);

  return (
    <div className="headerContainer">
      <div className="steps">
        <span className="currentStep">{step}</span>
        <span>/5</span>
      </div>
      <div className="stepsProgressBar">
        <div className="progress" />
      </div>
    </div>
  );
};
