import "./HateContent.scss";
import { ConfirmButton } from "../ConfirmButton/ConfirmButton";
import { useDispatch, useSelector } from "react-redux";
import { chooseHate, removeHate } from "../../redux/action";
import { useEffect, useState } from "react";

export const HateContent = () => {
  const [active, setActive] = useState(false);
  const [isCheckedOne, setIsCheckedOne] = useState(false);
  const [isCheckedTwo, setIsCheckedTwo] = useState(false);
  const [isCheckedThree, setIsCheckedThree] = useState(false);
  const [isCheckedFour, setIsCheckedFour] = useState(false);

  const hateArr = [
    "Lack of logic",
    "A slow speed",
    "Lack of humor",
    "Way too generic ending",
  ];

  const dispatch = useDispatch();
  const stateHateArr = useSelector((state) => state.hateArr);

  useEffect(() => {
    // const list = document.querySelectorAll("li");
    // forEach;
  }, []);

  const handleChecked = (e) => {
    const target = e.target.closest("li");

    switch (target.textContent) {
      case "Lack of logic":
        setIsCheckedOne(!isCheckedOne);
      case "A slow speed":
        setIsCheckedTwo(!isCheckedTwo);
      case "Lack of humor":
        setIsCheckedThree(!isCheckedThree);
      case "Way too generic ending":
        setIsCheckedFour(!isCheckedFour);
        break;

      default:
        break;
    }
    console.log(isCheckedOne);
    console.log(isCheckedTwo);
    console.log(isCheckedThree);
    console.log(isCheckedFour);

    hateArr.forEach((el) => {
      if (target.textContent === el) {
        if (target.classList.contains("checkedParent")) {
          target.classList.remove("checkedParent");
          target.children[1].classList.remove("checkedChild");
          target.children[1].style.backgroundColor = "#6d4376";

          const index = stateHateArr.indexOf(target.textContent);
          const newArr = [...stateHateArr];
          newArr.splice(index, 1);
          dispatch(removeHate(newArr));
          !newArr.length && setActive(false);
        } else {
          target.classList.add("checkedParent");
          target.children[1].classList.add("checkedChild");
          target.children[1].style.backgroundColor = "#e4229b";

          dispatch(chooseHate(target.textContent));
          setActive(true);
        }
      }
    });
  };

  const handleClick = () => {};

  return (
    <div className="hateContentContainer">
      <span className="hateQuestionContent">
        What do you <span>hate</span> the most in a book?
      </span>

      <ul className="listOfOption">
        {hateArr.map((el) => (
          <li key={el} onClick={(e) => handleChecked(e)}>
            <span>{el}</span>
            <div className="checkbox" />
          </li>
        ))}
      </ul>

      <ConfirmButton
        onClick={handleClick}
        goTo={!active || "/quiz/5"}
        active={active}
      >
        Next
      </ConfirmButton>
    </div>
  );
};
