import i18n from "i18next";
import { initReactI18next } from "react-i18next";
import LanguageDetector from "i18next-browser-languagedetector";
import Backend from "i18next-http-backend";

import translationEN from "../../public/locates/en/translation.json";
import translationDE from "../../public/locates/de/translation.json";
import translationFR from "../../public/locates/fr/translation.json";
import translationES from "../../public/locates/es/translation.json";

i18n
  .use(Backend)
  .use(LanguageDetector)
  .use(initReactI18next)
  .init({ debug: true, fallbackLng: "en" });

export default i18n;
