import {
  CHOOSE_LANGUAGE,
  CHOOSE_GENDER,
  CHOOSE_AGE,
  CHOOSE_HATE,
  CHOOSE_TOPICS,
  ADD_EMAIL,
} from "./action";

const defaultState = {
  language: "",
  gender: "",
  age: "",
  hate: [],
  topics: [],
  email: "",
};

export const reducer = (state = defaultState, action) => {
  switch (action.type) {
    case CHOOSE_LANGUAGE:
      return { ...state, language: action.payload };
    case CHOOSE_GENDER:
      return { ...state, gender: action.payload };
    case CHOOSE_AGE:
      return { ...state, age: action.payload };
    case CHOOSE_HATE:
      return { ...state, hate: [...state.hate, action.payload] };
    default:
      return state;
  }
};
