import i18n from "i18next";
import { initReactI18next } from "react-i18next";
import LanguageDetector from "i18next-browser-languagedetector";
import Backend from "i18next-http-backend";

import translationEN from "./assets/locates/es/translation.json";
import translationDE from "./assets/locates/de/translation.json";
import translationFR from "./assets/locates/fr/translation.json";
import translationES from "./assets/locates/es/translation.json";

const resources = {
  en: {
    translation: translationEN,
  },
  de: {
    translation: translationDE,
  },
  fr: {
    translation: translationFR,
  },
  es: {
    translation: translationES,
  },
};

i18n
  .use(Backend)
  .use(LanguageDetector)
  .use(initReactI18next)
  .init({ debug: true, fallbackLng: "en", resources });

export default i18n;
